sys_variable_value.value:
(function parse(inputs, outputs) {
    var item = JSON.parse(inputs.sourceItem);
    outputs.targetObject.type = item.type;
    outputs.targetObject.name = item.name;
    outputs.targetObject.id = item.id;
    outputs.targetObject.etag = item.etag;
    outputs.targetObject.size = item.size;
    outputs.targetObject.description = item.description;
    if (item.shared_link && item.shared_link.url)
        outputs.targetObject.shared_link = item.shared_link["url"]
    if (item.shared_link && item.shared_link["download_url"])
        outputs.targetObject.download_url = item.shared_link["download_url"]
    if (item.shared_link &&item.shared_link["access"])
        outputs.targetObject.access = item.shared_link["access"]
    if (item.parent && item.parent["id"])
        outputs.targetObject.parent_id = item.parent["id"]
})(inputs, outputs);

