sys_variable_value.value:
(function execute(inputs, outputs) {
    var metadata = {}
    if (inputs.marker)
        metadata["marker"] = inputs.marker
    metadata["from"] = inputs.from
    metadata["limit"] = 100
    metadata["fields"] = ["description","item_status","size","name","parent","shared_link"]
    metadata["ancestor_folder_id"] = inputs.ancestor_folder_id;
    if (inputs.query_params.value)
        metadata["query_params"] = inputs.query_params;
    if (inputs.order_by.field_key)
        metadata["order_by"] = inputs.order_by
    if (inputs.query)
        metadata["query"] = inputs.query
    outputs.meta_data = JSON.stringify(metadata);
})(inputs, outputs);

