sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new BoxUtils().trimStringInputs(inputs);
    outputs.folder_id = inputs.file_id;
    outputs.scope = inputs.scope;
    outputs.template_key = inputs.template_key
})(inputs, outputs);

