sys_variable_value.value:
(function execute(inputs, outputs) {
    var response = JSON.parse(inputs.response_body);
    if (response.type == "error") {
        outputs.error_message = response.message
        outputs.status = "Error"
    } else {
        var entries = response;
        if (entries) {
            var metadata = {}
            metadata["version"] = entries["$version"]
            metadata["parent"] = entries["$parent"]
            metadata["template"] = entries["$template"]
            metadata["scope"] = entries["$scope"]
            metadata["id"] = entries["$id"]
            outputs.folder_metadata_instance_on_file = metadata;
        }
        outputs.status = "Success"
    }
})(inputs, outputs);

