sys_variable_value.value:
(function execute(inputs, outputs) {

  if(inputs.status_code == 200) {    
    outputs.status = 'Success';
    var response = JSON.parse(inputs.response);
    outputs.shared_link_url = JSON.parse(inputs.response)['shared_link']['url'];
    outputs.shared_link_clickable = '[code]<p><a target="_blank" href="' + outputs.shared_link_url + '">' + outputs.shared_link_url + '</a></p>[/code]';
    outputs.shared_link_dwnld_url = JSON.parse(inputs.response)['shared_link']['download_url'];
    outputs.shared_link_info = inputs.response;     
    outputs.shared_link_object = response;
  } else {    
    outputs = new BoxErrorHandler().sharedLinkErrorHandler(inputs, outputs);
  }  

})(inputs, outputs);

