sys_variable_value.value:
(function execute(inputs, outputs) {
    var response = JSON.parse(inputs.response_body);
    if (response.type == "error") {
        outputs.error_message = response.message
        outputs.status = "Error"
    } else {
        var entries = response.entries;
        var folder_metadata = []
        if (entries){
            for (var index = 0; index < entries.length; index++) {
                var metadata = {}
                metadata["id"] = entries[index]["$id"]
                metadata["type"] = entries[index]["$type"]
                metadata["parent"] = entries[index]["$parent"]
                metadata["template"] = entries[index]["$template"]
                metadata["scope"] = entries[index]["$scope"]
                folder_metadata.push(metadata)
            }
          outputs.folder_metadata_5 = folder_metadata;
        }
        outputs.status = "Success"
    }
})(inputs, outputs);

