sys_variable_value.value:
(function execute(inputs, outputs) {
  	inputs = new BoxUtils().trimStringInputs(inputs);
	if(inputs.status_code == '201'){
    	outputs.status = "Success";
      var response = JSON.parse(inputs.response)['entries']
      	outputs.file_id = response[0]['id'];
    	outputs.file_info = inputs.response; 
      outputs.files_1  = response
    }else{
       	outputs = new BoxErrorHandler().fileErrorHandler(inputs,outputs);
     }
})(inputs, outputs);

