sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new BoxUtils().trimStringInputs(inputs);

    outputs.policy_id = inputs.policy_id;
    var policy_payload = {};
    if (inputs.release_note)
        policy_payload["release_note"] = inputs.release_note;
    if (inputs.policy_name)
        policy_payload["policy_name"] = inputs.policy_name;
    if (inputs.description)
        policy_payload["description"] = inputs.description;

  outputs.update_policy_payload = JSON.stringify(policy_payload);

})(inputs, outputs);

