sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == '200') {
    outputs.status = "Success";
    var response= JSON.parse(inputs.response);
    outputs.updated_legal_hold_policy = response;
  } else {
    outputs = new BoxErrorHandler().createUserErrorHandler(inputs, outputs);
  }
})(inputs, outputs);

