sys_variable_value.value:
(function execute(inputs, outputs) {

  var abort = false;
  if (inputs.status_code == 200 && !abort) {
    var total_count = JSON.parse(inputs.response)["entries"].length;
    for (var i = 0; i < total_count; i++) {
      if (inputs.user_name == JSON.parse(inputs.response)["entries"][i]["login"]) {
        outputs.status = "Success"
        outputs.user_id = JSON.parse(inputs.response)["entries"][i]["id"];
        abort = true;
      }
    }
    if (!abort) {
      outputs.status = "Error";
      outputs.error_message = "Provided username doesn't exist.";
    }

  } else {
    outputs = new BoxErrorHandler().genericErrorHandler(inputs, outputs);
  }

})(inputs, outputs);

