sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new BoxUtils().trimStringInputs(inputs);
    var retention_policy = {}
    retention_policy["policy_name"] = inputs.policy_name;
    retention_policy["policy_type"] = inputs.policy_type;
    retention_policy["disposition_action"] = inputs.disposition_action;
    if (inputs.retention_length)
        retention_policy["retention_length"] = inputs.retention_length;
    if (inputs.can_owner_extend_retention)
        retention_policy["can_owner_extend_retention"] = inputs.can_owner_extend_retention;
    if (inputs.are_owners_notified)
        retention_policy["are_owners_notified"] = inputs.are_owners_notified;
    if (inputs.custom_notification_recipients[0].name ||inputs.custom_notification_recipients[0].id ||inputs.custom_notification_recipients[0].login)
        retention_policy["custom_notification_recipients"] = inputs.custom_notification_recipients;

    outputs.retention_policy = JSON.stringify(retention_policy)

})(inputs, outputs);

