sys_variable_value.value:
(function execute(inputs, outputs) {
  var webhook_payload = {};
  webhook_payload["target"] = inputs.target;
  webhook_payload["address"] = inputs.address_url;
  webhook_payload["triggers"] = inputs.triggers
  outputs.webhook_payload = JSON.stringify(webhook_payload)
})(inputs, outputs);

