sys_variable_value.value:
(function execute(inputs, outputs) {

  if(inputs.status_code == '201'){
    outputs.status = 'Success';
    var response = JSON.parse(inputs.response)
    outputs.folder_id = response['id'];
    outputs.folder_info = inputs.response;    
    outputs.folder_1 = response;
  } else {        
    outputs = new BoxErrorHandler().folderErrorHandler(inputs,outputs);    
  }  

})(inputs, outputs);

