sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == '200') {
    outputs.status = "Success";
    var response = JSON.parse(inputs.response);
     outputs.group_id = response['id'];
     outputs.group_info = inputs.response;
    outputs.group_details_1 = response;
  } else {
    outputs = new BoxErrorHandler().groupErrorHandler(inputs, outputs);
  }
})(inputs, outputs);

