sys_variable_value.value:
(function execute(inputs, outputs) {


  inputs = new BoxUtils().trimStringInputs(inputs);
  outputs.group_id = inputs.group_id;

  var name = inputs.name ? inputs.name : undefined;
  var provenance = inputs.provenance ? inputs.provenance : undefined;
  var external_sync_identifier = inputs.external_sync_identifier ? inputs.external_sync_identifier : undefined;
  var description = inputs.description ? inputs.description : undefined;
  var invitability_level = inputs.invitability_level ? inputs.invitability_level : undefined;
  var member_viewability_level = inputs.member_viewability_level ? inputs.member_viewability_level : undefined;


  var payload2 = {};
  payload2["name"] = name;
  payload2["provenance"] = provenance;
  payload2["description"] = description;
  payload2["external_sync_identifier"] = external_sync_identifier;
  payload2["invitability_level"] = invitability_level;
  payload2["member_viewability_level"] = member_viewability_level;

  outputs.payload = JSON.stringify(payload2);

})(inputs, outputs);

