sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs = new BoxUtils().trimStringInputs(inputs);
  outputs.file_id = inputs.file_id
  outputs.file_name = inputs.file_name
  outputs.record = inputs.record
  outputs.version_id = inputs.version_id
  if (!inputs.version_id) {
    outputs.version_id = "current"
  } else {
    outputs.version_id = inputs.version_id
  }
})(inputs, outputs);

