sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == '204') {
    outputs.status = "Success";
  } else {
    outputs = new BoxErrorHandler().createGroupErrorHandler(inputs, outputs);

  }
})(inputs, outputs);

