sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == '201') {
        outputs.status = 'Success';
        var response = JSON.parse(inputs.response);
        outputs.metadata_id = response['$id'];
        outputs.metadata_information = inputs.response;
        var metadata_information = {};
        metadata_information["id"] = response["$id"];
        metadata_information["name"] = response["name"];
        metadata_information["version"] = response["$version"];
        metadata_information["type"] = response["$type"];
        metadata_information["parent"] = response["$parent"];
        metadata_information["scope"] = response["$scope"];
        outputs.file_metadata_1 = metadata_information;

    } else {
        outputs = new BoxErrorHandler().fileErrorHandler(inputs, outputs);
    }
})(inputs, outputs);

