sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == 201) {
    outputs.status = 'Success';
    var response = JSON.parse(inputs.response);
    outputs.collaboration_id = response['id']; 
    outputs.collaboration_json = inputs.response;
    outputs.collaborator_1 = response;
  } else {
    outputs = new BoxErrorHandler().collaborationErrorHandler(inputs,outputs);       
  }


})(inputs, outputs);

