sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new BoxUtils().trimStringInputs(inputs);
    var payload = {}
    payload["policy_id"] = inputs.policy_id
    payload["assign_to"] = inputs.assign_to
	outputs.assign_payload = JSON.stringify(payload);

})(inputs, outputs);

