sys_variable_value.value:
(function parse(inputs, outputs) {

 var item = JSON.parse(inputs.sourceItem);
 outputs.targetObject = item;

})(inputs, outputs);

(function execute(inputs, outputs) {
    if (inputs.marker)
        outputs.marker = inputs.marker
})(inputs, outputs);

