sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new BoxUtils().trimStringInputs(inputs);
    var policy_payload = {};
    policy_payload["policy_name"] = inputs.policy_name;
    policy_payload["description"] = inputs.description;
    if(inputs.ongoing)
    policy_payload["is_ongoing"] = inputs.ongoing;
    var selected_date = new GlideDateTime(inputs.end_date + "");
    var current_date = new GlideDateTime();
    if (selected_date.before(current_date)) {
        if (inputs.start_date) {
            var start_date = new GlideDateTime(inputs.start_date);
            var ms1 = start_date.getNumericValue();
            policy_payload["filter_started_at"] = new Date(ms1).toISOString()
        }
        if (inputs.end_date) {
            var end_date = new GlideDateTime(inputs.end_date);
            var ms2 = end_date.getNumericValue();
            policy_payload["filter_ended_at"] = new Date(ms2).toISOString()
        }

        outputs.policy_payload = JSON.stringify(policy_payload);

    } else throw new Error("Date should not exceed current date.")
})(inputs, outputs);

