sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == '201') {
        outputs.status = "Success";
        var response = JSON.parse(inputs.response);
        outputs.id = response['id'];
        outputs.group_info = inputs.response;
        outputs.group_details_1 = response;


    } else {
        outputs = new BoxErrorHandler().createGroupErrorHandler(inputs, outputs);

    }
})(inputs, outputs);

